import { useState } from 'react';
import './styles.css';

const mockData = [{
    id: 0,
    isComplete: false,
    description: 'create landing page',
}]

function List() {
    const [listData, setListData] = useState([]);

    const onChangeCheckBox = (data) => {
        const updatedData = listData.map(item => {
            if (item.id === data.id) {
                return {
                    ...item,
                    isComplete: item.isComplete ? false : true,
                }
            }

            return item;
        });

        console.log('updatedData', updatedData)

        setListData((prevData) => [
            ...prevData,
            updatedData,
        ]);
    }

    console.log('listData', listData)

    const listItem = () => {
        return listData.map((data, idx) => {
            return (
                <div key={idx}>
                    <label className="radioContainer" onClick={() => onChangeCheckBox(data)}>
                        {data.description}
                        <input type="radio" checked={data.status === 1} name="radio" />
                        <span className="checkmark"></span>
                    </label>
                </div>
            )
        })
    }

    return (
        <div className="container">
            <h1>To-do list</h1>
            {listItem()}
        </div>
    )
}

export default List;
