import { useState } from 'react';
import './styles.css';

const mockData = [{
    id: 0,
    isComplete: false,
    description: 'create landing page',
}]

function List() {
    const [listData, setListData] = useState(mockData);
    const [inputTodoValue, setInputTodoValue] = useState('');

    const onChangeCheckBox = (data) => {
        const updatedData = listData.map(item => {
            if (item.id === data.id) {
                return {
                    ...item,
                    isComplete: item.isComplete ? false : true,
                }
            }

            return item;
        });

        setListData(updatedData);
    }

    const listItem = () => {
        return listData.map((data, idx) => {            
            return (
                <div key={idx}>
                    <label className="radioContainer">
                        {data.description}
                        <input type="checkbox" checked={data.isComplete} name="radio" onClick={() => onChangeCheckBox(data)} />
                        <span className="checkmark"></span>
                    </label>
                </div>
            )
        })
    }

    const handleOnChangeInput = (e) => {
        const inputValue = e.target.value;
        setInputTodoValue(inputValue);
    }

    const handleAddNewTodo = () => {
        if (!inputTodoValue) return;

        const newTodoItem = {
            id: listData.length + 1,
            isComplete: false,
            description: inputTodoValue,
        };

        const updateTodoList = [...listData, newTodoItem];

        setListData(updateTodoList);
        setInputTodoValue('');
    }

    const addNewTodo = () => {
        return (
            <div className="addNewContainer">
                <input type="text" value={inputTodoValue} onChange={e => handleOnChangeInput(e)} />
                <button onClick={() => handleAddNewTodo()}>Add</button>
            </div>
        )
    }

    return (
        <div className="container">
            <h1>To-do list</h1>
            {addNewTodo()}
            {listItem()}
        </div>
    )
}

export default List;
