import { useState } from 'react';
import './styles.css';

const mockData = [{
    id: 0,
    isComplete: false,
    description: 'create landing page',
}]

function List() {
    const [listData, setListData] = useState(mockData);
    const [inputTodoValue, setInputTodoValue] = useState('');

    const onChangeCheckBox = (data) => {
        const updatedData = listData.map(item => {
            if (item.id === data.id) {
                return {
                    ...item,
                    isComplete: item.isComplete ? false : true,
                }
            }

            return item;
        });

        setListData(updatedData);
    }

    const listItem = () => {
        return listData.map((data, idx) => {
            return (
                <div key={idx}>
                    <label className="radioContainer">
                        {data.description}
                        <input type="radio" checked={data.isComplete} name="radio" onClick={() => onChangeCheckBox(data)} />
                        <span className="checkmark"></span>
                    </label>
                </div>
            )
        })
    }

    const addNewTodo = () => {
        return (
            <div>
                <input type="text" value={inputTodoValue} />
                <button></button>
            </div>
        )
    }

    return (
        <div className="container">
            <h1>To-do list</h1>

            {listItem()}
        </div>
    )
}

export default List;
