import './styles.css';

const mockData = [{
    id: 1,
    status: 0, /* status: 0: not done; 1: done */
    description: 'create landing page',
}]

function List() {
    const [listData, setListData] = useState(mockData);

    const onChangeCheckBox = (data) => {
        const updatedData = listData.map(item => {
            if (item.id === data.id) {
                return {
                    id: item.id,
                    status: item.id === 0 ? 1 : 0,
                    description: item.description,
                }
            }

            return item;
        });

        console.log('updatedData', updatedData)

        setListData(updatedData);
    }

    const listItem = () => {
        return mockData.map((data, idx) => {
            return (
                <div key={idx}>
                    <label className="radioContainer">
                        {data.description}
                        <input type="radio" checked={data.status === 0} name="radio" />
                        <span className="checkmark"></span>
                    </label>
                </div>
            )
        })
    }

    return (
        <div className="container">
            <h1>To-do list</h1>
            {listItem()}
        </div>
    )
}

export default List;
